import { useContext } from "react";
import { appleContext } from "../App";
type TaskProps = {
  task: {
    text: string;
  };
};

const Task = ({ task }: TaskProps) => {
  //3.add the useContext hook to the consumer children
  const user = useContext(appleContext);
  return (
    <div className="task">
      <h3>
        {user} buys {task.text}
      </h3>
    </div>
  );
};

export default Task;
